<html><head>	<title></title></head><body>	<?php		if (isset($_REQUEST['ok'])){			//cek keberadaan file			if ((!empty($_FILES['uploaded_file'])) && ($_FILES['uploaded_file']['error']==0)){				$filename = basename($_FILES['uploaded_file']['name']);				$ext = strtolower(substr($filename, strlen($filename) - 3, 3));				if (($ext == "jpg") && ($_FILES['uploaded_file']['type']=="image/jpeg") && ($_FILES['uploaded_file']['size'] < 350000)){					$uploaded_path = 'user_resources/'.$filename;					if (move_uploaded_file($_FILES['uploaded_file']['tmp_name'], $uploaded_path)){						echo "Upload Sukses";					}else{						echo 'Gagal upload';					}				}else{					echo "Hanya file JPEG atau ukuran file lebih dari 350KB";				}			}else{				echo "File tidak ada";			}		}	?>   <form action="upload_file.php" method="post" enctype="multipart/form-data">		<input type="file" name="uploaded_file"/>		<input type="submit" value="Upload" name="ok"/>		*.hanya file jpeg dengan ukuran < 350KB   </form></body></html>