<?php  echo date('D, d/M/Y'),"<br/>";  echo date('g:i:s a'),"<br/>";    $date1 = "20-7-2000";  $date2 = date("d-n-Y");  echo "tanggal 1: ".$date1."<br/>";  echo "tanggal 2: ".$date2."<br/>";  $t1 = explode('-',$date1);  $t2 = explode('-',$date2);    $dt1 = mktime(0,0,0,$t1[1],$t1[0],$t1[2]);  $dt2 = mktime(0,0,0,$t2[1],$t2[0],$t2[2]);    $result = abs(($dt2 - $dt1));  //86400 = 60 * 60 * 24;  $days = $result / 86400;  echo "Selisih hari: ".$days."hari<br/>";  //selisih jam  $jam = $days * 24;  echo "Selisih jam: ".$jam." jam<br/>";  //hitung umur  echo "Umur : ".floor($days/365) ." tahun<br/>";    //cek input tanggal  echo "31 April 2010".(checkdate(4, 31, 2010) ? ' Benar' : ' Salah')."<br/>";  echo "29 Februari 2012".(checkdate(2, 29, 2012) ? ' Benar' : ' Salah')."<br/>";?>